mapper.sql:
where 1=1


    and id ${idOperator} #{id}


    and business_id ${businessIdOperator} #{businessId}


    and daily_time ${dailyTimeOperator} #{dailyTime}


    and daily_time >= #{dailyTime1}


    and daily_time <= #{dailyTime2}


    and create_time ${createTimeOperator} #{createTime}


    and create_time >= #{createTime1}


    and create_time <= #{createTime2}


    and worktime ${worktimeOperator} #{worktime}


    and department_id ${departmentIdOperator} #{departmentId}


    and extinfo1 ${extinfo1Operator} #{extinfo1}


    and extinfo2 ${extinfo2Operator} #{extinfo2}


    and extinfo3 ${extinfo3Operator} #{extinfo3}


    and extinfo4 ${extinfo4Operator} #{extinfo4}


    and extinfo5 ${extinfo5Operator} #{extinfo5}


    and extinfo6 ${extinfo6Operator} #{extinfo6}


    and year ${yearOperator} #{year}


    and month ${monthOperator} #{month}

mapper.select:
select A.*,B.org_name from (select DEPARTMENT_ID as org_id,sum(worktime) as work_time from ST_BUSINESS_WORKTIME where DEPARTMENT_ID in
#{orgId}
and DAILY_TIME between #{startTime} and #{finishTime} group by DEPARTMENT_ID) A left join TS_ORG B ON A.org_id=B.ID

select sum(worktime) as work_time
from ST_BUSINESS_WORKTIME
where DEPARTMENT_ID in

    #{orgId}

and DAILY_TIME between #{startTime} and #{finishTime}

select a.worktime ,b.project_name,b.id from (select count(*),Business_id, sum(worktime) as worktime from ST_BUSINESS_WORKTIME where DEPARTMENT_ID in
#{orgId}
and DAILY_TIME between #{startTime} and #{finishTime} GROUP BY BUSINESS_ID) a left join TS_BUDGET b on a.business_id = b.id

select  from
	(select tmp_tb.* , rownum rn from
		(select  from ST_BUSINESS_WORKTIME


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
