mapper.sql:
where  DEL_FLAG != 1


    and update_user_id ${updateUserIdOperator} #{updateUserId}


    and update_user_name ${updateUserNameOperator} #{updateUserName}


    and ext_info1 ${extInfo1Operator} #{extInfo1}


    and ext_info2 ${extInfo2Operator} #{extInfo2}


    and ext_info3 ${extInfo3Operator} #{extInfo3}


    and ext_info4 ${extInfo4Operator} #{extInfo4}


    and leader_org_id ${leaderOrgIdOperator} #{leaderOrgId}


    and charge_org_id ${chargeOrgIdOperator} #{chargeOrgId}


    and project_manager_id ${projectManagerIdOperator} #{projectManagerId}


    and project_responsible_id ${projectResponsibleIdOperator} #{projectResponsibleId}


    and project_contact_id ${projectContactIdOperator} #{projectContactId}


    and project_specialist_id ${projectSpecialistIdOperator} #{projectSpecialistId}


    and project_member_ids ${projectMemberIdsOperator} #{projectMemberIds}


    and id ${idOperator} #{id}


    and research_project_id like '%'|| #{researchProjectId}|| '%'


    and project_no ${projectNoOperator} #{projectNo}


    and apply_year ${applyYearOperator} #{applyYear}


    and year ${yearOperator} #{year}


    and project_name like '%'|| #{projectName}|| '%'


    and finance_no ${financeNoOperator} #{financeNo}


    and project_level_dic_id ${projectLevelDicIdOperator} #{projectLevelDicId}


        and project_level_dic_name ${projectLevelDicNameOperator} #{projectLevelDicName}


    and project_origin ${projectOriginOperator} #{projectOrigin}


    and leader_org_name ${leaderOrgNameOperator} #{leaderOrgName}


    and charge_org_name ${chargeOrgNameOperator} #{chargeOrgName}


    and project_status_dic_id ${projectStatusDicIdOperator} #{projectStatusDicId}


        and project_status_dic_name ${projectStatusDicNameOperator} #{projectStatusDicName}


    and project_start_time ${projectStartTimeOperator} #{projectStartTime}


    and project_start_time >= #{projectStartTime1}


    and project_start_time <= #{projectStartTime2}


    and project_end_time ${projectEndTimeOperator} #{projectEndTime}


    and project_end_time >= #{projectEndTime1}


    and project_end_time <= #{projectEndTime2}


    and all_funds ${allFundsOperator} #{allFunds}


    and allocate_funds ${allocateFundsOperator} #{allocateFunds}


    and raise_funds ${raiseFundsOperator} #{raiseFunds}


    and project_manager_name ${projectManagerNameOperator} #{projectManagerName}


    and project_responsible_name ${projectResponsibleNameOperator} #{projectResponsibleName}


    and project_contact_name ${projectContactNameOperator} #{projectContactName}


    and project_contact_phone ${projectContactPhoneOperator} #{projectContactPhone}


    and project_specialist_name ${projectSpecialistNameOperator} #{projectSpecialistName}


    and project_member_names ${projectMemberNamesOperator} #{projectMemberNames}


    and create_time ${createTimeOperator} #{createTime}


    and create_time >= #{createTime1}


    and create_time <= #{createTime2}


    and create_user_id ${createUserIdOperator} #{createUserId}


    and create_user_name ${createUserNameOperator} #{createUserName}


    and update_time ${updateTimeOperator} #{updateTime}


    and update_time >= #{updateTime1}


    and update_time <= #{updateTime2}

