mapper.delete:
DELETE
FROM RS_HI_PROJECT_MEMBER
WHERE id_ = #{id}
  AND PROC_BUSINESS_KEY_ = #{procBusinessKey}

mapper.select:
select

from
(select tmp_tb.* , rownum rn from
(select

from RS_HI_PROJECT_MEMBER


    ${pager.orderCondition}

