mapper.sql:
where 1=1


    and id ${idOperator} #{id}


    and fund_template_name ${fundTemplateNameOperator} '%'||#{fundTemplateName}||'%'


    and create_user_id ${createUserIdOperator} #{createUserId}


    and create_user_name ${createUserNameOperator} #{createUserName}


    and create_time ${createTimeOperator} #{createTime}


    and create_time >= #{createTime1}


    and create_time <= #{createTime2}


    and modify_time ${modifyTimeOperator} #{modifyTime}


    and modify_time >= #{modifyTime1}


    and modify_time <= #{modifyTime2}

    and del_flag =0

    and ext1 ${ext1Operator} #{ext1}


    and ext2 ${ext2Operator} #{ext2}


    and ext3 ${ext3Operator} #{ext3}


    and ext4 ${ext4Operator} #{ext4}


    and ext5 ${ext5Operator} #{ext5}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from RS_FUND_RULES

		 and DEL_FLAG =0

		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
